import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { GameBoardComponent } from 'src/app/components/game-board/game-board.component';

@Component({
  selector: 'app-game',
  standalone: true,
  imports: [CommonModule, RouterLink, GameBoardComponent],
  templateUrl: './game.component.html',
  styleUrls: ['./game.component.scss'],
})
export class GameComponent {
  timeLeft: number = 3;
  interval: any;
  points: number = 0;
  showGame: boolean = false;
  endGame: boolean = false;
  startTimer() {
    this.showGame = true;
    this.interval = setInterval(() => {
      if (this.timeLeft >= 0.1) {
        this.timeLeft = Math.round((this.timeLeft - 0.1) * 10) / 10;
      } else {
        this.endGame = true;
        this.showGame = false;
      }
    }, 100);
  }

  pauseTimer() {
    clearInterval(this.interval);
  }
}
<ng-container *ngIf="!showGame; else show">
  <a routerLink="/homepage">go back to homepage</a>
  <button (click)="startTimer()">START GAME</button>
</ng-container>

<ng-template #show>
  <ng-container *ngIf="!endGame; else end">
    <div class="statsBar">
      <p>timeLeft: {{ timeLeft }}</p>
      <p>points: {{ points }}</p>
    </div>
    <app-game-board [(points)]="points"></app-game-board>
  </ng-container>
</ng-template>
<ng-template #end>
  You have aquired: {{ points }}
  <button>play again</button>
</ng-template>
