import { Component } from '@angular/core';
import { CommonModule, NgForOf } from '@angular/common';
import { GameTileComponent } from './game-tile/game-tile.component';

export type Tile = {
  text: string;
  clicked: boolean;
};

@Component({
  selector: 'app-game-board',
  standalone: true,
  imports: [CommonModule, GameTileComponent, NgForOf],
  templateUrl: './game-board.component.html',
  styleUrls: ['./game-board.component.scss'],
})
export class GameBoardComponent {
  listOfTiles: Tile[] = [
    { text: '1', clicked: false },
    { text: '2', clicked: false },
    { text: '3', clicked: false },
    { text: '4', clicked: false },
    { text: '5', clicked: false },
    { text: '1', clicked: false },
  ];

  onClick(index: number) {
    this.listOfTiles[index].clicked = !this.listOfTiles[index].clicked;
  }
}
<app-game-tile *ngFor="let tile of listOfTiles" [tile]="tile"></app-game-tile>
