import { Injectable } from '@angular/core';
import { Tile } from '../components/game-board/game-board.component';

@Injectable({
  providedIn: 'root',
})
export class TilesService {
  constructor() {}

  generateTiles(size: 6 | 9) {
    let TILES: Tile[] = [];
    while (TILES.length < size) {
      const randomNumber: string = Math.round(Math.random() * 100).toString();
      if (!TILES.includes({ text: randomNumber, clicked: false })) {
        TILES.push({
          text: randomNumber,
          clicked: false,
        });
      }
    }

    const randomIndex = (TILES[randomIndex] =
      structuredClone(TILES)[Math.floor(Math.random() * size)]);

    return TILES;
  }

  generateTwoRandomNumbers(size: 6 | 9): [number, number] {
    const randomOne = Math.floor(Math.random() * size);
    return [1, 2];
  }
}
