import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { GameBoardComponent } from 'src/app/components/game-board/game-board.component';

@Component({
  selector: 'app-game',
  standalone: true,
  imports: [CommonModule, RouterLink, GameBoardComponent],
  templateUrl: './game.component.html',
  styleUrls: ['./game.component.scss'],
})
export class GameComponent {
  timeLeft: number = 15;
  interval: any;

  startTimer() {
    this.interval = setInterval(() => {
      if (this.timeLeft > 0) {
        this.timeLeft -= 0.1;
      } else {
        return;
      }
    }, 100);
  }

  pauseTimer() {
    clearInterval(this.interval);
  }
}
<a routerLink="/homepage">go back to homepage</a>
<button (click)="startTimer()">START GAME</button>
<p>{{ timeLeft }}</p>
<app-game-board></app-game-board>
