import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { GameBoardComponent } from 'src/app/components/game-board/game-board.component';

@Component({
  selector: 'app-game',
  standalone: true,
  imports: [CommonModule, RouterLink, GameBoardComponent],
  templateUrl: './game.component.html',
  styleUrls: ['./game.component.scss'],
})
export class GameComponent {
  timeLeft: number = 15;
  interval: any;
  points: number = 0;
  showGame: boolean = false;
  endGame: boolean = false;
  startTimer() {
    this.showGame = true;
    this.interval = setInterval(() => {
      if (this.timeLeft >= 0) {
        this.timeLeft = Math.round((this.timeLeft - 0.1) * 10) / 10;
      } else {
        this.endGame = true;
      }
    }, 100);
  }

  pauseTimer() {
    clearInterval(this.interval);
  }
}
<a routerLink="/homepage">go back to homepage</a>
<button (click)="startTimer()" *ngIf="!showGame; else show">START GAME</button>
<ng-template #show>
  <div class="statsBar">
    <p>timeLeft: {{ timeLeft }}</p>
    <p>points: {{ points }}</p>
  </div>
  <app-game-board [(points)]="points"></app-game-board>
</ng-template>
